class Solution {
public:

    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<int, int>, vector<pair<int, int>>> pq;
        for(int i: horizontalCut) pq.push({i,0});
        for(int i: verticalCut) pq.push({i,1});
        int hct=1,vct=1;
        long long ans=0;
        while(!pq.empty()){
            pair<int,int>p=pq.top(); pq.pop();
            if(p.second==0) {ans+=p.first*vct; hct++;}
            else {ans+=p.first*hct; vct++;}   
        }
        return ans;
    }
};
