function minimumCost(m: number, n: number, horizontalCut: number[], verticalCut: number[]): number {
    horizontalCut.sort((a, b) => b - a);
    verticalCut.sort((a, b) => b - a);

    let hCuts = 1, vCuts = 1;
    let totalCost = 0;
    let h = 0, v = 0;
    while (h < horizontalCut.length && v < verticalCut.length) {
        if (horizontalCut[h] > verticalCut[v]) {
            totalCost += horizontalCut[h] * vCuts;
            h++;
            hCuts++; 
        } else {
            totalCost += verticalCut[v] * hCuts;
            v++;
            vCuts++; 
        }
    }

    while (h < horizontalCut.length) {
        totalCost += horizontalCut[h] * vCuts;
        h++;
        hCuts++;
    }

    while (v < verticalCut.length) {
        totalCost += verticalCut[v] * hCuts;
        v++;
        vCuts++;
    }

    return totalCost;
};
