class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        helper = []
        ho = 0
        ver = 0
        for val in h:
            helper.append([val, 0])
        for val in v:
            helper.append([val, 1])
        helper.sort()
        ans = 0
        while helper and (ho != m - 1 or ver != n - 1):
            val, typee = helper.pop()
            if(typee == 0):
                ans += val * (ver + 1)
                ho += 1
            else:
                ans += val * (ho + 1)
                ver += 1
        return ans
