class Solution:
    def minimumCost(
        self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]
    ) -> int:
        cuts = [(cut, True) for cut in horizontalCut] + [
            (cut, False) for cut in verticalCut
        ]
        cuts = sorted(cuts, key=lambda x: x[0], reverse=True)

        result = 0
        h_pieces = 1
        v_pieces = 1
        for cut, is_horizontal in cuts:
            if is_horizontal:
                result += cut * v_pieces
                h_pieces += 1
            else:
                result += cut * h_pieces
                v_pieces += 1
        return result
