class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(), horizontalCut.rend());
      sort(verticalCut.rbegin(), verticalCut.rend());

        int hIndex = 0, vIndex = 0;
        int horizontalPieces = 1, verticalPieces = 1;
        long long totalCost = 0;


        while (hIndex < horizontalCut.size() && vIndex < verticalCut.size()) {
            if (horizontalCut[hIndex] > verticalCut[vIndex]) {
                totalCost += horizontalCut[hIndex] * verticalPieces;
                horizontalPieces++;
                hIndex++;
            } else {
                totalCost += verticalCut[vIndex] * horizontalPieces;
                verticalPieces++;
                vIndex++;
            }
        }


        while (hIndex < horizontalCut.size()) {
            totalCost += horizontalCut[hIndex] * verticalPieces;
            hIndex++;
        }


        while (vIndex < verticalCut.size()) {
            totalCost += verticalCut[vIndex] * horizontalPieces;
            vIndex++;
        }

        return totalCost;
    }
};
