using ll = long long;
using pii = pair<int, int>;

class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& a, vector<int>& b) {
        vector<pii> v;
        for(auto &i : a) {
            v.emplace_back(i, 0);
        }
        for(auto &i : b) {
            v.emplace_back(i, 1);
        }
        vector<int> c(2);
        sort(v.rbegin(), v.rend());
        ll res = 0;
        for(auto &i : v) {
            res += (ll)i.first * (1 + c[1 - i.second]);
            c[i.second]++;
        }
        return res;
    }
};
