typedef long long ll;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<ll,ll>> pq;
        for(ll i:horizontalCut){
            pq.push({(ll)i,0});
        }
        for(ll i:verticalCut){
            pq.push({(ll)i,1});
        }
        ll h=1,v=1;
        ll ans=0;
        while(!pq.empty()){
            ll len=pq.top().first;
            ll type=pq.top().second;
            pq.pop();
            if(type){
                ans+=h*len;
                v++;
            }
            else{
                ans+=v*len;
                h++;
            }
        }
        return ans;
    }
};
