class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        arr = [(c,1) for c in horizontalCut] + [(c,0) for c in verticalCut]
        arr.sort(reverse=True,key=lambda val: val[0])
        a, b = 1, 1
        ans = 0
        for c, v in arr:
            if v:
                ans += c * b
                a+=1
            else:
                ans += c * a
                b += 1
        return ans
