class Solution:
    def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:
        res, cv, ch = 0, 0, 0

        hc.sort(reverse=True)
        vc.sort(reverse=True)

        i, j = 0, 0

        while i < m - 1 and j < n - 1:
            if hc[i] > vc[j]:
                res += hc[i] * (j + 1)
                i += 1
            else:
                res += vc[j] * (i + 1)
                j += 1

        while i < m - 1:
            res += hc[i] * (j + 1)
            i += 1

        while j < n - 1:
            res += vc[j] * (i + 1)
            j += 1

        return res
