#define ll long long
class Solution {
public:
    long long minimumCost(int M, int N, vector<int>& H, vector<int>& V) {
        priority_queue<array<ll, 3>> pq;
        for(ll i = 0; i < N - 1; ++i)
            pq.push({V[i], i, 1});
        for(ll i = 0; i < M - 1; ++i)
            pq.push({H[i], i, 0});
        ll ans = 0, h = 1, v = 1;
        while(!pq.empty()) {
            auto [x, i, t] = pq.top(); pq.pop();
            if(t == 0) {
                ans += (x * v);
                h++;
            } else {
                ans += (x * h);
                v++;
            }
        }
        return ans;
    }
};
