class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>> vp;
        for(auto h:horizontalCut) {
            vp.push_back({h,0});
        }
        for(auto v:verticalCut) {
            vp.push_back({v,1});
        }
        sort(vp.begin(),vp.end());
        int h = 1;
        int v = 1;
        int x = vp.size();
        long long res = 0;
        for(int i = x-1;i>=0;i--) {
            int t = vp[i].second;
            if(t==1) {
                res = res + (long long)v*(long long)vp[i].first;
                h++;
            } else {
                res = res + (long long)h*(long long)vp[i].first;
                v++;
            }
        }
        return res;
    }
};
