class Solution {
public:
    typedef long long ll;
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        typedef pair<ll,ll> ppi;
        priority_queue<ppi> pq;
        for(auto it : horizontalCut){
            pq.push({it,0});
        }
        for(auto it : verticalCut){
            pq.push({it,1});
        }
        ll hor = 1 , ver = 1;
        ll cost = 0;
        while(!pq.empty()){
            auto it = pq.top();
            pq.pop();
            ll dir = it.second;
            ll ct = it.first;
            if(dir==0){
                cost+=ct*ver;
                hor++;
            }
            else {
                cost+=ct*hor;
                ver++;
            }
        }
        return cost;
    }
};
