class Solution {
public:
    using L = long long;
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        priority_queue<pair<L,L>> pq;
        for(int i = 0; i < m-1; i ++){
            pq.push({a[i], 0});
        }
        for(int i = 0; i < n-1; i ++){
            pq.push({b[i], 1});
        }
        L xcnt{}, ycnt{};
        L ans{};
        while(pq.size()){
            auto [x, y] = pq.top();
            pq.pop();
            if(y == 0){
                ans += x * (ycnt+1);
                xcnt++;
            }else{
                ans += x * (xcnt+1);
                ycnt++;
            }
        }
        return ans;

    }
};
