class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        using i64 = long long;
        i64 ans = 0;
        ranges::sort(horizontalCut);
        ranges::sort(verticalCut);
        ranges::reverse(horizontalCut);
        ranges::reverse(verticalCut);
        horizontalCut.push_back(0);
        verticalCut.push_back(0);
        int i = 0, j = 0;
        int cnti = 0, cntj = 0;
        while (true) {
            if (horizontalCut[i] > verticalCut[j]) {
                ans += horizontalCut[i++] * (1ll + cnti);
                cntj++;
            } else {
                ans += verticalCut[j++] * (1ll + cntj);
                cnti++;
            }
            if (i == m || j == n) break;
        }
        return ans;
    }
};
