class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort()
        verticalCut.sort()

        answer = 0

        h_sum, v_sum = sum(horizontalCut), sum(verticalCut)

        for _ in range(m + n - 2):
            if not horizontalCut:
                x = verticalCut.pop()
                answer += x
                v_sum -= x
            elif not verticalCut or (horizontalCut[-1] > verticalCut[-1]):
                x = horizontalCut.pop()
                answer += x + v_sum
                h_sum -= x
            else:
                x = verticalCut.pop()
                answer += x + h_sum
                v_sum -= x

        return answer
