#include <bits/stdc++.h>
using namespace std;

class Solution {
   public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        swap(n, m);
        priority_queue<array<int, 2>> q;
        for (auto c : horizontalCut) {
            q.push({c, 0});
        }
        for (auto c : verticalCut) {
            q.push({c, 1});
        }
        int x = 1, y = 1;
        long long res = 0;
        while (!q.empty()) {
            auto [c, type] = q.top();
            q.pop();
            if (type == 0) {
                res += (long long)y * c;
                x += 1;
            } else {
                res += (long long)x * c;
                y += 1;
            }
        }
        return res;
    }
};
