class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        List<long[]> list = new ArrayList<>();
        for (int k : horizontalCut) {
            list.add(new long[]{k, 0});
        }

        for (int i : verticalCut) {
            list.add(new long[]{i, 1});
        }
        list.sort((a, b) -> Long.compare(b[0],a[0]));

        long result = 0;
        long horiz = 1;
        long vertic = 1;

        for (long[] cut : list) {
            long cost = cut[0];
            if (cut[1] == 0) {
                result += cost * vertic;
                horiz++;
            } else {
                result += cost * horiz;
                vertic++;
            }
        }
        return result;

    }
}
