class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hCnt, vCnt = 1, 1
        ans = 0
        horizontalCut = [(h, 0) for h in horizontalCut]
        verticalCut = [(v, 1) for v in verticalCut]
        cut = horizontalCut + verticalCut
        cut.sort(reverse=True)
        for c, t in cut:
            if t == 0:
                vCnt += 1
                ans += c * hCnt
            else:
                hCnt += 1
                ans += c * vCnt
        return ans
