#include <bits/stdc++.h>
using namespace std;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int> &horizontalCut, vector<int> &verticalCut) {
        using pii = pair<int, int>;
        long long col = 1, row = 1;
        priority_queue<pii> pq;
        for (int num : horizontalCut) {
            pq.emplace(num, 0);
        }
        for (int num : verticalCut) {
            pq.emplace(num, 1);
        }
        long long ans = 0;
        while (!pq.empty()) {
            auto [price, type] = pq.top();
            pq.pop();
            if (type == 0) {
                ans += row * price;
                col++;
            } else {
                ans += col * price;
                row++;
            }
        }

        return ans;
    }
};
