class Solution:
    def minimumCost(
        self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]
    ) -> int:
        horizontalCut.sort()
        verticalCut.sort()
        total_cost = h = v = 0
        while horizontalCut and verticalCut:
            if horizontalCut[-1] > verticalCut[-1]:
                total_cost += horizontalCut.pop() * (v + 1)
                h += 1
            else:
                total_cost += verticalCut.pop() * (h + 1)
                v += 1
        while horizontalCut:
            total_cost += horizontalCut.pop() * (v + 1)
        while verticalCut:
            total_cost += verticalCut.pop() * (h + 1)
        return total_cost
