class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long h = 1;
        long long v = 1;

        priority_queue<pair<int, bool>> pq;
        for (auto& p: horizontalCut)
            pq.push({p, true});
        for (auto& p: verticalCut)
            pq.push({p, false});

        long long res = 0;
        while (!pq.empty()) {
            auto [p, isH] = pq.top();
            pq.pop();

            if (isH)
            {
                res += p * h;
                v++;
            } else {
                res += p * v;
                h++;
            }
        }

        return res;
    }
};
