class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = [(-horizontalCut[i], 0) for i in range(m - 1)] + [
            (-verticalCut[j], 1) for j in range(n - 1)
        ]
        cuts.sort()
        h_count, v_count = 1, 1
        cost = 0
        for i in range(m - 1 + n - 1):
            c, t = cuts[i]
            if t == 0:
                cost -= h_count * c
                v_count += 1
            else:
                cost -= v_count * c
                h_count += 1
        return cost
