class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        int h=1, v=1;
        priority_queue<pair<int,int>>pq;
        for (int i=0; i<a.size(); i++) pq.push({a[i],0}); //0 means horizontal
        for (int i=0; i<b.size(); i++) pq.push({b[i],1});
        long long ans=0;
        while(!pq.empty()){
            int cut=pq.top().first;
            int fl=pq.top().second;
            pq.pop();
            if (fl==0){
                ans+=(v*1LL*cut);
                h++;
            }
            else{
                ans+=(h*1LL*cut);
                v++;
            }
        }
        return ans;
    }
};
