class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& aaa, vector<int>& bbb) {
        vector<pair<int,int>> a;
        for(auto v:aaa)
            a.push_back({v,0});
        for(auto v:bbb)
            a.push_back({v,1});
        sort(a.begin(),a.end());
        reverse(a.begin(),a.end());
        #define ll long long
        ll ans=0;
        ll x=1;
        ll y=1;
        for(auto [u,v]:a){
            if(v==0){
                ans+=u*y;
                ++x;
            }else{
                ans+=u*x;
                ++y;
            }
        }
        return ans;
    }
};
