class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ind = list(range(m-1)) + list(range(m-1, m+n-2))
        lst = horizontalCut+verticalCut
        row = 1
        col = 1
        ind.sort(key=lambda it: -lst[it])
        ans = 0
        for i in ind:
            if i < m-1:
                ans += col*horizontalCut[i]
                row += 1
            else:
                ans += row * verticalCut[i-m+1]
                col += 1

        return ans
