class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        int xiorra = 0, messi = 0;
        long long xiorraCount = 1, messiCount = 1;
        long long cost = 0;

        while (xiorra < horizontalCut.size() && messi < verticalCut.size()) {
            if (horizontalCut[xiorra] >= verticalCut[messi]) {
                cost += static_cast<long long>(horizontalCut[xiorra]) * messiCount;
                xiorraCount++;
                xiorra++;
            } else {
                cost += static_cast<long long>(verticalCut[messi]) * xiorraCount;
                messiCount++;
                messi++;
            }
        }

        while (xiorra < horizontalCut.size()) {
            cost += static_cast<long long>(horizontalCut[xiorra]) * messiCount;
            xiorraCount++;
            xiorra++;
        }

        while (messi < verticalCut.size()) {
            cost += static_cast<long long>(verticalCut[messi]) * xiorraCount;
            messiCount++;
            messi++;
        }

        return cost;
    }
};
