class Solution {
public:
    typedef long long ll;
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end(), greater<ll>());
        sort(v.begin(), v.end(), greater<ll>());

        ll t = 0;
        ll i = 0, j = 0;
        ll a = 1, b = 1;

        while (i < h.size() && j < v.size()) {
            if (h[i] >= v[j]) {
                t += h[i] * b;
                i++;
                a++;
            } else {
                t += v[j] * a;
                j++;
                b++;
            }
        }

        while (i < h.size()) {
            t += h[i] * b;
            i++;
        }

        while (j < v.size()) {
            t += v[j] * a;
            j++;
        }

        return t;
    }
};
