class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end(),greater<int>());
        sort(v.begin(), v.end(),greater<int>());
        long long a=0,b=0,s=0;
        while(a<h.size()&&b<v.size()){
            if(h[a]>v[b]){
                s+=(b+1)*h[a++];
            }
            else{
                s+=(a+1)*v[b++];
            }
        }
        while(a<h.size()){

                s+=(b+1)*h[a++];
        }
        while(b<v.size())
                s+=(a+1)*v[b++];
        return s;
    }
};
