class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        sort(hor.begin(), hor.end(), greater<int>());
        sort(ver.begin(), ver.end(), greater<int>());

        int horizontalSegments = 1, verticalSegments = 1;

        int horIndex = 0, verIndex = 0;

        long long totalCost = 0;

        while (horIndex < hor.size() || verIndex < ver.size()) {
            if (horIndex == hor.size()) {
                totalCost += horizontalSegments * ver[verIndex++];
            }
            else if (verIndex == ver.size()) {
                totalCost += verticalSegments * hor[horIndex++];
            }
            else {
                if (ver[verIndex] >= hor[horIndex]) {
                    totalCost += horizontalSegments * ver[verIndex++];
                    verticalSegments++;
                }
                else {
                    totalCost += verticalSegments * hor[horIndex++];
                    horizontalSegments++;
                }
            }
        }
        return totalCost;
    }
};
