class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        std::vector<std::array<int, 3>> a;
        for (int i = 0; i + 1 < m; ++i) a.push_back({hc[i], 0, i});
        for (int i = 0; i + 1 < n; ++i) a.push_back({vc[i], 1, i});
        std::ranges::sort(a, std::greater<>());

        long long ans = 0;
        long long hcnt = 0, vcnt = 0;
        for (auto [v, t, i]: a) {
            if (t == 0) {
                ++hcnt;
                ans += (vcnt + 1) * v;
            } else {
                ++vcnt;
                ans += (hcnt + 1) * v;
            }
        }

        return ans;
    }
};
