class Solution:
    def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:
        h_mul = 1
        v_mul = 1

        hc.sort()
        vc.sort()

        res = 0

        while hc and vc:
            if hc[-1] >= vc[-1]:
                res += hc.pop() * h_mul
                v_mul += 1
            else:
                res += vc.pop() * v_mul
                h_mul += 1

        if hc:
            res += sum(hc) * h_mul
        if vc:
            res += sum(vc) * v_mul

        return res
