class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<int> h, v;
        h.push(-2147483648);
        v.push(-2147483648);
        for(int i : horizontalCut) h.push(i);
        for(int i : verticalCut) v.push(i);
        long long ans = 0;
        while(h.size() > 1 || v.size() > 1) {
            long long hn = h.top();
            long long vn = v.top();
            long long hc = n - v.size() + 1;
            long long vc = m - h.size() + 1;
            if(hn > vn) {
                ans += hn * hc;
                h.pop();
            }
            else {
                ans += vn * vc;
                v.pop();
            }
        }
        return ans;
    }
};
