class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h,v=1,1
        cuts=[(c,'H') for c in horizontalCut]+[(c,'V') for c in verticalCut]
        cuts.sort(reverse=True, key=lambda x: x[0])
        cost = 0
        for c, t in cuts:
            if t=='H':
                cost+=c * v
                h+=1
            else:
                cost+=c * h
                v+=1
        return cost
