class Solution:
    def minimumCost(self, m: int, n: int, h_c: List[int], v_c: List[int]) -> int:
        h_t = 1
        v_t = 1
        h_c.sort(reverse=True)
        v_c.sort(reverse=True)
        l = 0
        r = 0
        res = 0
        while l < len(h_c) and r < len(v_c):
            if h_c[l] > v_c[r]:
                res += h_c[l] * h_t
                v_t += 1
                l += 1
            else:
                res += v_c[r] * v_t
                h_t += 1
                r += 1
        if l < len(h_c):
            for i in range(l, len(h_c)):
                res += (h_c[i] * h_t)
        if r < len(v_c):
            for i in range(r, len(v_c)):
                res += (v_c[i] * v_t)
        return res
