class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts=[]
        for i in horizontalCut:
            cuts.append((i,0))
        for i in verticalCut:
            cuts.append((i,1))

        cuts.sort(reverse=True)

        hpieces=vpieces=1        
        total=0

        for c, d in cuts:
            # horizontal
            if d==0:
                total+=c*vpieces
                hpieces+=1
            # vertical
            else:
                total+=c*hpieces
                vpieces+=1
        return total
