class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ans = 0
        horizontalCut.sort()
        verticalCut.sort()
        h = v = 1
        while horizontalCut and verticalCut:
            if horizontalCut[-1] >= verticalCut[-1]:
                ans += v*horizontalCut.pop()
                h += 1
            else:
                ans += h*verticalCut.pop()
                v += 1
        while horizontalCut:
            ans += v*horizontalCut.pop()
        while verticalCut:
            ans += h*verticalCut.pop()
        return ans
