class Solution {
    #define ll long long
public:
    long long minimumCost(int l, int w, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(begin(horizontalCut),end(horizontalCut));
        sort(begin(verticalCut),end(verticalCut));
        ll i1 = l-2,i2 = w-2;
        ll ans = 0;
        ll x = 1,y = 1;
        while(i1>=0 && i2>=0)
        {
            if(horizontalCut[i1] > verticalCut[i2])
            {
                ans += x*1ll*horizontalCut[i1];
                ++y,--i1;
            }
            else ans += y*1ll*verticalCut[i2--],++x;
        }
        while(i1>=0)
        {
            ans += (x*1ll*horizontalCut[i1--]),++y;
        }
        while(i2>=0)
        {
            ans += (y*1ll*verticalCut[i2--]);
        }
        return ans;
    }
};
