class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ans, cnt, cuts = 0, [0, 0], []
        for i, x in enumerate(horizontalCut):
            cuts.append((x, 0))
        for i, x in enumerate(verticalCut):
            cuts.append((x, 1))
        cuts.sort(reverse=True)
        for x, y in cuts:
            cnt[y] += 1
            ans += x * (cnt[1 ^ y] + 1)
        return ans
