class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        vc = 1
        hc = 1
        cuts = [(h, True) for h in horizontalCut] + [(v, False) for v in verticalCut]
        cuts.sort(reverse = True)
        res = 0
        for c, ih in cuts:
            if ih:
                res += c * vc
                hc += 1
            else:
                res += c * hc
                vc += 1
        return res
