using ll = long long;
#define all(a) a.begin(), a.end()

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        vector<array<int, 2>> p;
        for (int x: a) {
            p.push_back({x, 0});
        }
        for (int x: b) {
            p.push_back({x, 1});
        }
        sort(all(p), greater<>());
        int c[2]{};
        ll ans = 0;
        for (auto& [x, y]: p) {
            ans += 1ll * x * (c[y ^ 1] + 1);
            c[y] ++;
        }
        return ans;
    }
};
