class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
          sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());

        int hCuts = 1,vCuts = 1;
        int i = 0,j = 0;
        long long cost = 0;
        while(i < h.size() && j < v.size()){
            if(h[i] > v[j]){
                cost += h[i] * hCuts;
                ++vCuts;
                ++i;
            }else{
                cost += v[j] * vCuts;
                ++hCuts;
                ++j;
            }
            // cout << cost << endl;
        }
        while(i < h.size()){
            cost += h[i] * hCuts;
            ++i;
        }

        while(j < v.size()){
            cost += v[j] * vCuts;
            ++j;
        }
        return cost;
    }
};
