class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = [(i, 0) for i in horizontalCut] + [(i, 1) for i in verticalCut]
        cuts.sort(reverse=True, key=lambda x: x[0])

        hp = 1
        vp = 1

        ans = 0

        for cost, typ in cuts:
            if typ == 0:
                ans += cost * vp
                hp += 1
            else: 
                ans += cost * hp
                vp += 1

        return ans
