class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        together = []
        for i in range(m - 1):
            together.append((horizontalCut[i], 'h'))

        for i in range(n - 1):
            together.append((verticalCut[i], 'v'))

        together.sort(reverse = True)

        horizontal_multiplier = 1
        vertical_multiplier = 1
        res = 0
        for i in range(m + n - 2):
            cost, direction = together[i]
            if direction == 'h':
                res += cost * horizontal_multiplier
                vertical_multiplier += 1
            else:
                res += cost * vertical_multiplier
                horizontal_multiplier += 1

        return res
