typedef long long ll;
class Solution {
public:
    long long minimumCost(int hm,int hn,vector<int> &hhorizontalCut,vector<int> &hverticalCut){
        vector<pair<int,bool>> hcuts;
        for(int hcost:hhorizontalCut){
            hcuts.push_back({hcost,true});
        }
        for(int hcost:hverticalCut){
            hcuts.push_back({hcost,false});
        }
        sort(hcuts.rbegin(),hcuts.rend());
        ll hhPieces=1,hvPieces=1,hresans=0;
        for(auto hcut:hcuts){
            if(hcut.second){
                hresans+=(ll)hcut.first*hvPieces;
                hhPieces++;
            }
            else{
                hresans+=(ll)hcut.first*hhPieces;
                hvPieces++;
            }
        }
        return hresans;
    }
};
