class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long int,bool>>cuts;
        for(auto it:horizontalCut)
        {
            cuts.push_back({it,true});
        }
        for(auto it:verticalCut)
        {
            cuts.push_back({it,false});
        }
        sort(cuts.begin(),cuts.end(),[](const pair<long long int,bool>& a,const pair<long long int,bool>& b){
            return a.first>b.first;
        });
        long long int ans=0;
        long long int h=1;
        long long int v=1;
        for(auto it:cuts)
        {
            if(it.second){
                ans+=it.first*v;
                h++;
            }
            else{
                ans+=it.first*h;
                v++;
            }
        }
        return ans;
    }
};
