class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<long long,long long>> pq;

        long long h = 1;
        long long v = 1;

        for(auto it:horizontalCut)
        {
            pq.push({it,0});
        }
        for(auto it:verticalCut)
        {
            pq.push({it,1});
        }

        long long sum = 0;

        while(!pq.empty())
        {
            auto top = pq.top();
            pq.pop();

            long long cost = top.first;
            long long dir = top.second;

            if(dir == 0)
            {
                sum += h * cost;
                v++;
            }
            else{
                sum += v * cost;
                h++;
            }
        }

        return sum;
    }
};
