class Solution {
public:
    typedef long long LL;
    #define pb push_back
    long long minimumCost(int n, int m, vector<int>& row, vector<int>& col) {
        vector <pair <int,int> > v;
        for (int x : row) v.pb ({x,1});
        for (int x : col) v.pb ({x,2});
        sort (v.begin (),v.end (),greater <> ());
        LL ans = 0;
        int r = 1,c = 1;
        for (auto [x,y] : v) {
            if (y == 1) ans += (LL)x * c,r++;
            else ans += (LL)x * r,c++;
        }
        return ans;
    }
};
