class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        int Cols = 1,Rows = 1;
        long long Cost = 0;
        sort(begin(h),end(h),greater<int>());
        sort(begin(v),end(v),greater<int>());
        int i = 0,j = 0;
        n = h.size(),m = v.size();
        while (i<n && j<m){
            if (h[i]>v[j]){
                Cost += 1LL*Cols*h[i++];
                Rows++;
            }
            else{
                Cost += 1LL*Rows*v[j++];
                Cols++;
            }
        }
        while (i<n){
            Cost += 1LL*Cols*h[i++];
            Rows++;
        }
        while (j<m){
            Cost += 1LL*Rows*v[j++];
            Cols++;
        }
        return Cost;
    }
};
