class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans = 0;
        swap(m,n);
        m--;n--;
        sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());
        int i = 0,j = 0;
        while(i < n || j < m){
            int l = -1,r = -1;
            if(i < n) l = max(l,h[i]);
            if(j < m) r = max(r,v[j]);
            if(l > r){
                ans += 1LL * l * (j + 1);
                i++;
            } else {
                ans += 1LL * r * (i + 1);
                j++;
            }
        }
        return ans;
    }
};
