class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ans = 0
        rows, cols = 1, 1
        cuts = sorted(chain(((cut, True) for cut in horizontalCut), ((cut, False) for cut in verticalCut)),
                      key = lambda item: item[0], reverse = True)
        for cut, direction in cuts:
            if direction:
                ans += cols * cut
                rows += 1
            else:
                ans += rows * cut
                cols += 1
        return ans
